package com.wipro.basic;

public class TerinaryOp {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num=45;
		
		String result=(num>=1)?"even":"odd";
		if(num==0) {
			System.out.println("Number is zero");
		}
		else {
			System.out.println(result);
		}
	}

}
